$(window).scroll(function() {    
    var scroll = $(window).scrollTop();
    var topofDiv = $(".banner").offset().top; //gets offset of header
    var height = $(".banner").outerHeight(); //gets height of header
     
    if (scroll >= (topofDiv + height) - 80) { 
          $("#topNav").addClass("scrolled");
          $("#mainLogo").addClass("scrolled");
          // $(".sideNav").removeClass("fixed");
      } else {
          $("#topNav").removeClass("scrolled");
          $("#mainLogo").removeClass("scrolled");
          // $(".sideNav").removeClass("fixed");
      }
});

$( "#mobile-nav-toggle" ).click(function() {
  $( ".mobile-nav" ).toggleClass( "slide-open" );
  $( "#mobile-nav-toggle" ).toggleClass( "active" );
  // $( ".mobile-bg" ).toggleClass( "active" );
});

